nrp:
	{$nrpSessId}

	{$nrpMasterSession}

	{section name=i loop=$nrpMainErrors}
	{$nrpMainErrors[i]}
	{/section}

	{section name=j loop=$nrpMainAlerts}
	{$nrpMainAlerts[j]}
	{/section}

	{section name=l loop=$nrpMainTime}
	{$nrpMainTime[l]}
	{/section}

	{$nrpUserId}

	{section name=t loop=$nrpMainType}

		{$nrpMainType[t]}
		{$nrpMainTypeColor[t]}
		{$nrpMainTypeIcon[t]}

	{/section}

	{section name=x loop=$nrpMainDaysOfWeek}
	{$nrpMainDaysOfWeek[x]}
	{/section}

	{section name=v loop=$nrpMainDays}

		{$nrpMainDays[v][2]}
		{$nrpMainDays[v][3]}
		{$nrpMainDays[v][4]}
		{$nrpMainDays[v][6]}

	{/section}

{section name=o loop=$nrpMainApp}


			{$nrpSpanStartDay}
			{$nrpSpanStartMonth}
			{$nrpSpanStartYear}
			{$nrpSpanStartDayOfWeek}


			{$nrpSpanEndDay}
			{$nrpSpanEndMonth}
			{$nrpSpanEndYear}
			{$nrpSpanEndDayOfWeek}

	{section name=k loop=$nrpMainApp[o]}

			{$nrpMainApp[o][k][1]}
			{$nrpMainApp[o][k][2]}
			{$nrpMainApp[o][k][3]}

				{$nrpMainApp[o][k][4]}
				{$nrpMainApp[o][k][5]}
				{$nrpMainApp[o][k][6]}

			{$nrpMainApp[o][k][8]}
			{$nrpMainApp[o][k][10]}
			{$nrpMainApp[o][k][12]}
			{$nrpMainApp[o][k][13]}
			{$nrpMainApp[o][k][14]}
			{$nrpMainApp[o][k][16]}
			{$nrpMainApp[o][k][17]}
			{$nrpMainApp[o][k][18]}
			{$nrpMainApp[o][k][20]}
			{$nrpMainApp[o][k][21]}
			{$nrpMainApp[o][k][22]}
			{$nrpMainApp[o][k][23]}

	{/section}

{/section}

	{section name=n loop=$nrpGroups}

		{$nrpGroups[n][2]}
		{$nrpGroups[n][1]}
		{$nrpGroups[n][3]}
		{$nrpGroups[n][4]}
		{section name=o loop=$nrpGroups[n][5]}

			{$nrpGroups[n][5][o][1]}
			{$nrpGroups[n][5][o][2]}

		{/section}

	{/section}
